package seleniumDay9;

public class ThreadSleep {

	public static void main(String[] args) throws InterruptedException{
		// TODO Auto-generated method stub
		System.out.println("one");
		Thread.sleep(2000);
		System.out.println("one");
		Thread.sleep(2000);

		System.out.println("one");
		Thread.sleep(2000);

		System.out.println("one");

	}

}
